    private void jBtnConcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtnConcluirActionPerformed
        /*
        INSERT INTO APP.HOTELRESERVAS (IDHOTEL, NOMBREHOTEL, NUMHAB, TIPOHAB, SERVICIOHOTEL, IDCLIENTES) 
	VALUES (2, NULL, NULL, NULL, NULL, NULL)
        */
        
        ResultSet resultados = HappyTravel.base.consultar("SELECT max(IDHOTEL) as maximo FROM APP.HOTELRESERVAS");
        ResultSet resultados2 = HappyTravel.base.consultar("SELECT max(IDCLIENTES) as maximo FROM APP.CLIENTES");
            
        int i = 0,j=0;
        if (resultados != null) {
                try {
                    while (resultados.next()) {
                        i = Integer.parseInt(resultados.getString("maximo"));
                    }
                } catch (Exception e) {
                    e.printStackTrace();
                }
            }
        
        if (resultados2 != null) {
                try {
                    while (resultados2.next()) {
                        j = Integer.parseInt(resultados2.getString("maximo"));
                    }
                } catch (Exception e) {
                    e.printStackTrace();
                }
            }
        
        if (HappyTravel.base.ejecutar("INSERT INTO APP.HOTELRESERVAS (IDHOTEL, "
                + "NOMBREHOTEL, "
                + "NUMHAB, "
                + "TIPOHAB, "
                + "SERVICIOHOTEL, "
                + "IDCLIENTES) \n" 
                + "VALUES (" + (i + 1) + ", "
                + "'" + Reservacion.jComHotel.getSelectedItem() + "', "
                + ""+jHabitacion.getSelectedIndex()+", "
                + "'"+jTipoHab.getSelectedItem()+"', "
                + "'"+jServicio.getSelectedItem()+"', "
                + ""+j+")")){
            System.out.println("eEjecuci贸n correcta!");
            this.setVisible(false);
        }
else {
        System.out.println("Ocurri贸 un problema al ejecutar!");
        }
        
    }//GEN-LAST:event_jBtnConcluirActionPerformed

